import React from 'react';

const Navbar = () => {
  return (
    <nav className="bg-[#b0def1] px-10 py-4 flex items-center justify-between shadow">
      {/* Logo */}
      <div className="font-serif italic text-xl font-bold leading-tight">
        <div>Ticket</div>
        <div className="ml-1">Please?</div>
      </div>

      {/* Center section: Search + Menu */}
      <div className="flex items-center gap-6">
        {/* Search bar */}
        <input
          type="text"
          placeholder="Search"
          className="px-3 py-1 rounded-md border border-gray-300 w-[280px] focus:outline-none"
        />

        {/* Create Event - as clickable text */}
        <a href="#" className="text-sm font-medium hover:underline cursor-pointer">
          Create Event
        </a>

        {/* Categories - hover to show dropdown */}
        <div className="relative group">
          <span className="text-sm font-medium hover:underline cursor-pointer">
            Categories
          </span>
          <div className="absolute hidden group-hover:block mt-2 bg-white text-sm text-gray-700 rounded-xl shadow-lg p-4 w-44 right-0 z-10 border border-gray-300">
            <ul className="space-y-1">
              <li className="hover:underline cursor-pointer">Concert</li>
              <li className="hover:underline cursor-pointer">Sports</li>
              <li className="hover:underline cursor-pointer">Art, comedy, theater</li>
              <li className="hover:underline cursor-pointer">Food & Drink</li>
            </ul>
          </div>
        </div>
      </div>

      {/* Login/Register button */}
      <button className="bg-white text-blue-700 border border-blue-500 px-3 py-1 rounded-md text-sm font-medium hover:bg-blue-100 transition-all">
        Login/Register
      </button>
    </nav>
  );
};

export default Navbar;
