import React from 'react';

const events = [
  { title: 'Charlie Chaplin Show', img: '/posters/events/chaplin.png' },
  { title: 'Peppa Pig Day Out', img: '/posters/events/peppa.png' },
  { title: 'Mystery DJ Night', img: '/posters/events/dj.png' },
  { title: 'Local Street Market', img: '/posters/events/market.png' }
];

const PopularEvents = () => {
  return (
    <div className="px-10 py-10 bg-[#f8f7f5] mt-12 rounded-t-xl">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-xl font-bold">Popular Events</h2>
        <a href="#" className="text-sm text-blue-600 hover:underline">See all events</a>
      </div>
      <div className="grid grid-cols-2 sm:grid-cols-4 gap-6">
        {events.map((event, i) => (
          <div key={i} className="flex flex-col items-center bg-white p-3 rounded-md shadow-md">
            <img
              src={event.img}
              alt={event.title}
              className="w-[160px] h-[120px] object-cover rounded-md"
            />
            <p className="mt-2 text-center text-sm font-medium">{event.title}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PopularEvents;
