import React, { useState } from 'react';

const posters = [
  '/posters/events/harrypotter.png',
  '/posters/events/hope.png',
  '/posters/events/taeyoon.png',
  '/posters/events/chaplin.png',
  '/posters/events/dj.png',
  '/posters/events/market.png'
];

const Carousel = () => {
  const [index, setIndex] = useState(0);

  const next = () => {
    if (index + 3 < posters.length) setIndex(index + 1);
  };

  const prev = () => {
    if (index > 0) setIndex(index - 1);
  };

  return (
    <div className="relative px-10 mt-6">
      <div className="flex items-center">
        <button
          onClick={prev}
          className="text-2xl px-2"
          disabled={index === 0}
        >
          ◀
        </button>

        <div className="flex gap-4 overflow-hidden w-full justify-center">
          {posters.slice(index, index + 3).map((src, i) => (
            <img
              key={i}
              src={src}
              alt={`poster-${i}`}
              className="w-[300px] h-[180px] object-cover rounded-lg shadow-md"
            />
          ))}
        </div>

        <button
          onClick={next}
          className="text-2xl px-2"
          disabled={index + 3 >= posters.length}
        >
          ▶
        </button>
      </div>

      <div className="flex justify-center mt-3 gap-1">
        {Array.from({ length: posters.length - 2 }).map((_, i) => (
          <span
            key={i}
            className={`w-3 h-3 rounded-full ${i === index ? 'bg-blue-500' : 'bg-gray-300'}`}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default Carousel;
