import React, { useState, useEffect } from "react";
import ContactForm from "./contactForm";
import "./index.css"; // Contains modal styles

function App() {
  const [users, setUsers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);

  // Fetch users on component mount
  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = () => {
    fetch("http://127.0.0.1:5000/users")
      .then((res) => res.json())
      .then((data) => setUsers(data.users))
      .catch((err) => console.error("Failed to fetch users:", err));
  };

  const openModal = (user = null) => {
    setSelectedUser(user);
    setShowModal(true);
  };

  const closeModal = () => {
    setSelectedUser(null);
    setShowModal(false);
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Contact List</h1>
      <button
        className="bg-blue-500 text-white px-4 py-2 rounded mb-4"
        onClick={() => openModal()}
      >
        Create Contact
      </button>

      <div id="contact-list" className="space-y-2">
        {users.map((user) => (
          <div key={user.id} className="bg-white p-4 shadow rounded flex justify-between">
            <p>{user.name} ({user.role}) - {user.email}</p>
            <button
              className="bg-yellow-400 px-3 py-1 rounded"
              onClick={() => openModal(user)}
            >
              Edit
            </button>
          </div>
        ))}
      </div>

      {showModal && (
        <div id="modal" className="modal">
          <div className="modal-content">
            <span id="close-modal" className="close" onClick={closeModal}>&times;</span>
            <div id="contact-form-container">
              <ContactForm user={selectedUser} onClose={closeModal} />
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
