import React from 'react';

const posters = [
  '/posters/events/harrypotter.png',
  '/posters/events/hope.png',
  '/posters/events/taeyoon.png'
];

const Carousel = () => {
  return (
    <div className="mt-8 flex flex-col items-center">
      <div className="flex gap-4 overflow-x-auto px-4">
        {posters.map((src, index) => (
          <img
            key={index}
            src={src}
            alt={`poster-${index}`}
            className="w-[300px] h-[180px] rounded-lg shadow-md object-cover"
          />
        ))}
      </div>

      <div className="flex gap-1 mt-4">
        {posters.map((_, i) => (
          <span key={i} className="w-3 h-3 bg-blue-400 rounded-full"></span>
        ))}
      </div>
    </div>
  );
};

export default Carousel;
