import React from 'react';

const Navbar = () => {
  return (
    <nav className="bg-[#b0def1] px-10 py-4 flex items-center justify-between shadow-md">
      {/* Logo */}
      <div className="text-left leading-tight font-serif text-xl font-bold italic">
        <div>Ticket</div>
        <div className="ml-2">Please?</div>
      </div>

      {/* Center: Search + Actions */}
      <div className="flex-1 flex items-center justify-center gap-4">
        <input
          type="text"
          placeholder="Search"
          className="px-3 py-1 rounded-md border border-gray-300 w-[300px] focus:outline-none"
        />
        <button className="text-sm font-semibold">Create Event</button>

        {/* Dropdown */}
        <div className="relative group">
          <button className="text-sm font-semibold">Categories</button>
          <div className="absolute hidden group-hover:block mt-2 bg-white text-sm text-gray-700 rounded-xl shadow-lg p-4 w-48 right-0 z-10 border border-gray-300">
            <ul className="space-y-1">
              <li className="hover:underline cursor-pointer">Concert</li>
              <li className="hover:underline cursor-pointer">Sports</li>
              <li className="hover:underline cursor-pointer">Art, comedy, theater</li>
              <li className="hover:underline cursor-pointer">Food & Drink</li>
            </ul>
          </div>
        </div>
      </div>

      {/* Login/Register */}
      <button className="bg-white text-blue-700 border border-blue-500 px-3 py-1 rounded-md text-sm font-medium hover:bg-blue-100 transition-all">
        Login/Register
      </button>
    </nav>
  );
};

export default Navbar;
