import React from 'react';

const Navbar = () => {
  return (
    <nav className="bg-[#b0def1] px-10 py-4 flex items-center justify-between shadow-md">
      {/* Left side: Logo + Center options in ONE LINE */}
      <div className="flex items-center gap-6 w-full">
        {/* Logo (stacked visually but doesn't break line) */}
        <div className="leading-tight font-serif text-lg font-bold italic mr-6 text-nowrap">
          <span className="block">Ticket</span>
          <span className="block">Please?</span>
        </div>

        {/* Search bar */}
        <input
          type="text"
          placeholder="Search"
          className="px-3 py-1 rounded-md border border-gray-300 w-[250px] focus:outline-none"
        />

        {/* Create Event */}
        <a href="#" className="text-sm font-medium hover:underline whitespace-nowrap">
          Create Event
        </a>

        {/* Categories dropdown */}
        <div className="relative group whitespace-nowrap">
          <span className="text-sm font-medium hover:underline cursor-pointer">
            Categories
          </span>
          <div className="absolute hidden group-hover:block mt-2 bg-white text-sm text-gray-700 rounded-xl shadow-lg p-4 w-44 right-0 z-10 border">
            <ul className="space-y-1">
              <li className="hover:underline cursor-pointer">Concert</li>
              <li className="hover:underline cursor-pointer">Sports</li>
              <li className="hover:underline cursor-pointer">Art, comedy, theater</li>
              <li className="hover:underline cursor-pointer">Food & Drink</li>
            </ul>
          </div>
        </div>
      </div>

      {/* Right side: Login/Register */}
      <div className="ml-6">
        <button className="bg-white text-blue-700 border border-blue-500 px-3 py-1 rounded-md text-sm font-medium hover:bg-blue-100 transition-all">
          Login/Register
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
